import React from "react";

interface TextProps {
    color?: string
    type?: 'h1' | 'h2' | 'h3' | 'h4' | 'paragraph' | 'span'
}
const Text = ({type, color}: TextProps) => {

    switch(type) {
        case 'h1':
            return <h1 style={{color: color ? color : `$title-color`}}>H1</h1>
        case 'h2':
            return <h2>H2</h2>
        case 'h3':
            return <h3>H3</h3>
        case 'h4':
            return <h4>H4</h4>
        case 'paragraph':
            return <p>P</p>
        case 'span':
            return <span>SPAN</span>
        default:
            return <span>Default</span>
        // code block
    }
}

export default Text
